//the dimension of latent representations
val k = 2

//regularization parameter
val lambda = 0.05

implicit val random = new scala.util.Random(1l)

//the domain of parameters
@domain case class Theta(relations: IndexedSeq[Vect], pairs: IndexedSeq[Vect])

//the set of possible parameters
implicit val Thetas = Theta.Values(Seqs(Vectors(k), cols.size), Seqs(Vectors(k), rows.size))

//the class of cells
@domain case class Cell(s: Int, i: Int, j: Int)

//the set of possible cells
implicit val Cells = Cell.Values(Ints(0 until rows.length), Ints(0 until entities.length), Ints(0 until entities.length))

//mapping cell argument indices to entity-pair index
def cellToPairIx(term: Cells.Term) = term.map(c => argsIxToPairIx(c.i -> c.j))

//mapping training data (s, i, j) to values of the Cells domain
val trainingData = triples.map(t => Cell(t._1, t._2, t._3)).toConst(Cells)

//samples an unobserved cell for a given relation
def sampleNegCell(pos: Cell): Cell = {
  def inner(attempts: Int): Cell = {
    val row = random.nextInt(rows.size)
    val (i,j) = tupleToArgIndices(rows(row))
    val sample = Cell(pos.s, i, j)
    if (attempts == 0) {
      println("Couldn't sample a negative cell for " + pos)
      sample
    } else if (trainingData.contains(sample)) {
      inner(attempts - 1)
    } else sample
  }
  inner(100)
}

//the score of one cell
def score(t: Thetas.Term)(cell: Cells.Term): DoubleTerm =
  t.relations(cell.s) dot t.pairs(cellToPairIx(cell))

//training loss 
def loss(t: Thetas.Term) = {
  //we sample a positive cell, and memoize the result
  val pos = mem(trainingData.sampleShuffled)
  //based on the memoized positive cell, we sample a negative cell      which needs to be memoized because it will reappear several times
  val neg = mem(pos.map(sampleNegCell))
  //regularization term
  val l2 = sum(Seq(t.relations(pos.s), t.pairs(cellToPairIx(pos)), t.pairs(cellToPairIx(neg)))) { v => v.l2() * -lambda }

  log(sigm(score(t)(pos))) + log(sigm(-score(t)(neg))) + l2
}

